mapper.select:
SELECT b.bord_id, b.bord_catg, c.catg_name, b.bord_titl, b.bord_cont, b.user_id, u.nickname, b.bord_date, b.bord_hitc, b.bord_wrip, f.file_cnt, bc.comment_cnt, (SELECT COUNT(bord_id) FROM board WHERE 1=1
AND bord_titl LIKE CONCAT('%', #{search}, '%') OR bord_cont LIKE CONCAT('%', #{search}, '%')
AND bord_catg = #{category}
) cnt FROM board b LEFT OUTER JOIN users u ON b.user_id = u.user_id LEFT OUTER JOIN category c ON c.catg_id = b.bord_catg LEFT OUTER JOIN ( SELECT bord_id, COUNT(file_id) file_cnt FROM board_file GROUP BY bord_id ) f ON f.bord_id = b.bord_id LEFT OUTER JOIN ( SELECT bord_id, COUNT(comm_id) comment_cnt FROM board_comment GROUP BY bord_id ) bc ON bc.bord_id = b.bord_id WHERE 1 = 1
AND bord_titl LIKE CONCAT('%', #{search}, '%') OR bord_cont LIKE CONCAT('%', #{search}, '%')
AND bord_catg = #{category}
ORDER BY bord_id DESC LIMIT #{pageSize} OFFSET #{startRow}

mapper.update:
UPDATE board
SET bord_catg = #{bordCatg}, bord_titl = #{bordTitl}, bord_cont = #{bordCont}
WHERE bord_id = #{bordId}

UPDATE board
SET bord_hitc = bord_hitc + 1
WHERE bord_id = #{bordId}

mapper.delete:
DELETE FROM board_comment 
WHERE 1=1


	AND comm_id = #{commId}


	AND bord_id = #{bordId}
