mapper.select:
SELECT b.bord_id, b.bord_catg, c.catg_name, b.bord_titl, b.bord_cont, b.user_id, b.bord_date, b.bord_hitc, b.bord_wrip, (SELECT COUNT(bord_id) FROM board WHERE 1=1
AND bord_titl LIKE CONCAT('%', #{search}, '%') OR bord_cont LIKE CONCAT('%', #{search}, '%')
AND bord_catg = #{category}
) cnt FROM board b LEFT OUTER JOIN category c ON c.catg_id = b.bord_catg WHERE 1 = 1
AND bord_titl LIKE CONCAT('%', #{search}, '%') OR bord_cont LIKE CONCAT('%', #{search}, '%')
AND bord_catg = #{category}
ORDER BY bord_id DESC LIMIT #{pageSize} OFFSET #{startRow}
